        body: {
          fontSize: 14,
        },
      }))(TableCell);

      const StyledTableRow = withStyles((theme) => ({
        root: {
          '&:nth-of-type(odd)': {

          },
        },
      }))(TableRow);

      const useStyles = makeStyles((theme) => ({
        paper: {
          marginTop: theme.spacing(8),
          display: 'flex',
          flexDirection: 'column',

        },
        table: {
          minWidth: "100%",
        },
        form: {
          width: '100%', // Fix IE 11 issue.
          marginTop: theme.spacing(3),
        marginRight: theme.spacing(2),
        },
        submit: {
          margin: theme.spacing(3, 0, 2),
          backgroundColor: theme.palette.error.dark,
        },
      }));
      function createData(name) {
        return { name};
      }

      const classes = useStyles();
      const history = useHistory()
      function getCurrentDate(separator=''){

        let newDate = new Date()
        let date = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();

        return `${year}${separator="-"}${month<10?`0${month}`:`${month}`}${separator="-"}${date}`
      }
      const fecha = getCurrentDate();

      const ImagenRef = useRef([])

      const [data, setData] = useState([]);
      const [data1, setData1] = useState();
      const [p,setp] = useState()
      const imagenes = useRef([])

	  const convertBase64 = (file) => {
	    return new Promise((resolve, reject) => {
	      const fileReader = new FileReader();
	      fileReader.readAsDataURL(file)
	      fileReader.onload = () => {
	        resolve(fileReader.result);
	      }
	      fileReader.onerror = (error) => {
	        reject(error);
	      }
	    })
	  }

      const handleChange = async(e) =>{
        let index = e.target.name
        const base64 = await convertBase64(e.target.files[0])
    	console.log(base64)
    	imagenes.current[index] = base64
    	setData1(base64)
      }

	const [AutobusEconomico, setautobus] = useState(); 
      useEffect(() => {getAuto()},[]);
      const getAuto = async () => {
      	var currentLocation = window.location.pathname;
      	if(currentLocation == "/reffafoto")
      	if(localStorage.getItem("Autobus"))
      	{
      		setautobus(localStorage.getItem("Autobus"))
	      	const obj = {
	      		URL: 'fallos/getFallos',
	      		NumeroEconomico: localStorage.getItem("Autobus")
	      	}
	        const _data = await getFallos(obj)
	        if (_data.success)
	        {
		         //console.log("HOLIS")
	          setData(_data.data)
	          //console.log(data)
	          //setPreload(false)
	        }
	      	localStorage.removeItem("Autobus")
	      }
	      else 
			swal("¡Error!",'Usa el boton "anterior" para seleccionar un formato.', "warning")
      }
const Flag = useRef()


const Envio = async() => {
	swal({
            title: "¿Está seguro que desea finalizar?",
            text: 'Al dar click en el botón "siguiente" no podrá volver a subir fotos para este formato.',
            icon: "warning",
            buttons: {
                catch: {
                        text: "Cancelar",
                        value: "cancel"
                        },
                catch1: {
                        text: "Siguiente",
                        value: "siguiente"
                        },
                }
          })
          .then(async(value) => {
            if(value == "siguiente")
            {
		await Computo()
		history.replace("/reffaindice")
	    }
	});
}
      const Computo =  async() => {
          /*swal({
            title: "¿Está seguro que desea finalizar?",
            text: 'Al dar click en el botón "siguiente" no podrá volver a subir fotos para este formato.',
            icon: "warning",
            buttons: {
		catch: {
			text: "Cancelar",
			value: "cancel"
			},
		catch1: {
			text: "Siguiente",
			value: "siguiente"
			},
		}
          })
          .then((value) => {
            if(value == "siguiente")
            {*/
          for(let i = 0; i < data.length; i++)
              {
                const formData = {
                  NumeroEconomico: data[i].NumeroEconomico,
                  Foto: imagenes.current[i],
                  Codigo: data[i].Codigo,
                  Fecha: data[i].Fecha,
                }
                console.log("Alo " + i)
                console.log(formData)
                if(imagenes.current[i])
                {
                  sendFoto(formData).then(response => {
                    console.log("Enviando...")
                    console.log("Fin...")  
                  })
                }
              }/*
              history.replace("/reffaindice")
              swal("¡Felicidades!","Tus datos se enviaron correctamente. ", "success")
	    }
	    });*/
      }


    return (
    <Container component="main">

    <Typography component="h1" variant="h5" align="left">
      Fotos para anexo formato REFFA del autobus {AutobusEconomico}
    </Typography>
    <Typography component="h1" variant="h5" align="left">
      {fecha}
    </Typography>
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
              <TableHead>
                <TableRow>
                  <StyledTableCell align="center">Código</StyledTableCell>
                  <StyledTableCell align="center">Localización</StyledTableCell>
                  <StyledTableCell align="center">Foto</StyledTableCell>
                  <StyledTableCell align="center">Seleccionar foto</StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
              {data.map((row,index) => (
                  <StyledTableRow key={row.NumeroEconomico}>
                    <StyledTableCell align="center" component="th" scope="row">
                    {row.Codigo}
                    </StyledTableCell>
                    <StyledTableCell align="center">{row.Ubicaciones}</StyledTableCell>
                    <StyledTableCell align="center">
                    <img src={imagenes.current[index]} style={{
			          height: 100,
			          width: 200
			        }}/>
                    </StyledTableCell>
                    <StyledTableCell align="center">
                     <TextField
		                id="originalFileName"
		                type="file"
		                inputProps={{ accept: 'image/*' }}
		                required
		                label="Document"

		                name={index}
		                inputRef={(el) => (ImagenRef.current[index] = el)}
		                onChange={handleChange}
		                size="small"
		                variant="standard"
		              >

		     </TextField>
                     </StyledTableCell>                 
                  </StyledTableRow>
                ))}
              </TableBody>
              </Table>
      &nbsp;&nbsp;
    </TableContainer>
    <Button
            width= "25%"
            align= "left"
            variant="contained"
            color="secondary"
            className={classes.submit}
            type="submit"
            onClick={()=> {
                history.push('/reffaindice')
            }}>
          Anterior
    </Button>
    <Button
	    type="submit"
	    width= "25%"
	    align= "right"
		style={{float:"right"}}
	    variant="contained"
	    color="secondary"
	    className={classes.submit}
	    onClick={Envio}
    >
          Finalizar
    </Button>
    </Container>
    );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<REFFAF />, rootElement);

export default withRouter(REFFAF);
